import { Component } from '@angular/core';
import { Content } from '../helper-files/content-interface';

@Component({
  selector: 'app-content-list',
  templateUrl: './content-list.component.html',
  styleUrls: ['./content-list.component.css']
})
export class ContentListComponent {
  contents: Content[] = [];
  constructor(){
    const content1:Content={
      id:1,
      title:'Title1',
      description: 'Des1',
      creator: 'Creator1'
    }
    const content2:Content={
      id:2,
      title:'Title2',
      description: 'Des2',
      creator: 'Creator2'
    }
    const content3:Content={
      id:3,
      title:'Title3',
      description: 'Des3',
      creator: 'Creator3'
    }
    const content4:Content={
      id:4,
      title:'Title4',
      description: 'Des4',
      creator: 'Creator4'
    }
    const content5:Content={
      id:5,
      title:'Title5',
      description: 'Des5',
      creator: 'Creator5'
    }
    const content6:Content={
      id:6,
      title:'Title6',
      description: 'Des6',
      creator: 'Creator6'
    }
    this.contents.push(content1);
    this.contents.push(content2);
    this.contents.push(content3);
    this.contents.push(content4);
    this.contents.push(content5);
  }
}
