import { Content } from "./content-interface";

export class ContentList {
    private contentList: Content[];
  
    constructor() {
      this.contentList = [];
    }
  
    public get list(): Content[] {
      return this.contentList;
    }
  
    public add(content: Content): void {
      this.contentList.push(content);
    }
  
    public count(): number {
      return this.contentList.length;
    }
  
    public getHtmlAtIndex(index: number): string {
      const content = this.contentList[index];
      const imgTag = `<img src="${content.imgURL}" alt="${content.title}">`;
      return `
        <div>
          <h2>${content.title}</h2>
          <p>${content.description}</p>
          <p>Creator: ${content.creator}</p>
          <p>Type: ${content.type}</p>
          ${imgTag}
        </div>
      `;
    }
  }
